import React, { useRef, useState } from "react";
import "./SignupScreen.css";
import firebase from "firebase";
import auth from "../../firebase";

function SignupScreen() {
  const [signup, setsignup] = useState(true);
  const emailref = useRef(null);
  const passwordref = useRef(null);

  const switchAuthModeHandler = () => {
    setsignup((prevState) => !prevState);
  };
  const register = (e) => {
    e.preventDefault();
    const enteredemail = emailref.current.value;
    const enteredpassword = passwordref.current.value;
    firebase
      .auth()
      .createUserWithEmailAndPassword(enteredemail, enteredpassword)
      .then((userCredential) => {
        console.log(userCredential);
        
      })
      .catch((error) => {
        console.log(error.message);
      });
      
      firebase.auth().signInWithEmailAndPassword(enteredemail,enteredpassword)
  .then((userCredential) => {
    // Signed in
   console.log(userCredential) ;
    // ...
  })
  .catch((error) => {
    console.log(error.message) ;
  });
  };

  return (
    <div className="SignupScreen">
      <form>
        <h1>{signup ? "Sign up" : "Login In"}</h1>
        <input type="text" placeholder="Enter Your Email" ref={emailref} />
        <input
          type="password"
          placeholder="Enter Your Password"
          ref={passwordref}
        />
       {signup && <button onClick={register}>Sign Up</button>} 
        {!signup && <button>Login in</button>}
        <h2>
          <span onClick={switchAuthModeHandler}>
            {signup ? "Sign up" : "Login In"}
          </span>
        </h2>
      </form>
    </div>
  );
}

export default SignupScreen;




