import React, { useState } from 'react'
import { useEffect } from 'react'
import './Row.css'
import axios from 'axios'
function Row({title,isLargeRow}) {

  const [movies,setmovie]=useState([])

useEffect(()=>{
  async function fetchdata(){
    try{
      const res = await axios.get('https://api.themoviedb.org/3/trending/all/week?api_key=32ce6adcd8f4737faa4cf686afa6b000&language=en-US')
      console.log(res.data.results);
      let d=res.data.results
      setmovie(res.data.results)
    } catch(error){
      console.log(error)
    }  
  }
  fetchdata();
},[])
 
  return (
    <div className='row'>
      <h1>{title}</h1>
      
      <div className="row_posters">
        {
          movies.map(movie=>{
            return <img src={`https://image.tmdb.org/t/p/original/${movie.poster_path}`} className='ri' />
          })
        }
       
      </div>
    </div>
  )
}

export default Row


