import axios from 'axios';
import React, { useEffect, useState } from 'react'
import './Banner.css'
import userrequest from '../tmdbfile/request';

function Banner() {
  const [movie,setmovie]=useState(false);

useEffect(()=>{
  async function fetchdata(){
           
         const res = await axios.get('https://api.themoviedb.org/3/trending/all/week?api_key=32ce6adcd8f4737faa4cf686afa6b000&language=en-US')
          const random = Math.floor(Math.random()*res.data.results.length)-1
          console.log(res.data.results[random]);
          setmovie(res.data.results[random])
  }
  fetchdata();
},[])
  
  return (
    <div className='banner'>
         <div className="banner_content">
            <h1>Movie Name</h1>
            <div className='btn'>
            <button >Play</button>
